import React, { useState } from "react";
import "./Card.css";
import { motion, AnimateSharedLayout } from "framer-motion";
// parent Card
const [selectedCards, setSelectedCards] = useState([]);

const Card = (props) => {
  const [selectedCards, setExpanded] = useState(false);
  return (
    <AnimateSharedLayout>
      {expanded ? (
        <SelectCard param={props} selectedCards={() => setExpanded(false)} />
      ) : (
        <CompactCard param={props} selectedCards={() => setExpanded(false)} />
      )}
    </AnimateSharedLayout>
  );
};

// Compact Card
function CompactCard({ param, selectedCards }) {
  const Png = param.png;
  return (
    <motion.div
      className="CompactCard"
      style={{
        background: param.color.backGround,
        boxShadow: param.color.boxShadow,
      }}
      layoutId="expandableCard"
      onClick={selectedCards}
    >
      <div className="radialBar">

        <span>{param.title}</span>
      </div>
      <div className="detail">
        <span>{param.img}</span>
      </div>
    </motion.div>
  );
}

