import React, { useState } from "react";
import "./Card.css";
import { motion, AnimateSharedLayout } from "framer-motion";
import { useNavigate } from 'react-router-dom';

const Card = (props) => {
  const [selected, setSelected] = useState([]);
  const navigate = useNavigate();

  const toggleSelect = (id) => {
    if (selected.includes(id)) {
      setSelected(selected.filter((sel) => sel !== id));
    } else {
      setSelected([...selected, id]);
    }
  };

  const handleExpanded = (id) => {
    if (!selected.includes(id)) {
      toggleSelect(id);
    }
  };

  const handleCompact = (id) => {
    toggleSelect(id);
  };

  const handleNavigate = () => {
    history.push("/selected");
  };

  return (
    <AnimateSharedLayout>
      {props.data.map((card) => {
        const isSelected = selected.includes(card.id);

        return (
          <React.Fragment key={card.id}>
            {isSelected ? (
              <SelectCard
                param={card}
                handleToggle={() => toggleSelect(card.id)}
                handleNavigate={handleNavigate}
              />
            ) : (
              <CompactCard
                param={card}
                handleToggle={() => toggleSelect(card.id)}
                handleExpanded={() => handleExpanded(card.id)}
              />
            )}
          </React.Fragment>
        );
      })}
    </AnimateSharedLayout>
  );
};

function CompactCard({ param, handleToggle, handleExpanded }) {
  const Png = param.png;

  return (
    <motion.div
      className="CompactCard"
      style={{
        background: param.color.backGround,
        boxShadow: param.color.boxShadow,
      }}
      layoutId={`card-${param.id}`}
      onClick={handleExpanded}
    >
      <div className="codeBox">
        <span>{param.title}</span>
        <button onClick={handleToggle}>Select</button>
      </div>
      <div className="detail">
        <span>{param.img}</span>
      </div>
    </motion.div>
  );
}

function SelectCard({ param, handleToggle, handleNavigate }) {
  const data = {};

  return (
    <motion.div
      className="ExpandedCard"
      style={{
        background: param.color.backGround,
        boxShadow: param.color.boxShadow,
      }}
      layoutId={`card-${param.id}`}
    >
      <span>{param.title}</span>
      <button onClick={handleToggle}>Deselect</button>
      <button onClick={handleNavigate}>Go to selected cards</button>
    </motion.div>
  );
}

export default Card;
